# This removes autogenerated CMake, Make, and executable files

# TODO: this might not resolve simlinks, test on linux/windows
DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )"

# remove files
rm ${DIR}/CMakeCache.txt      -f
rm ${DIR}/CMakeFiles/         -fr
rm ${DIR}/cmake_install.cmake -f
rm ${DIR}/Makefile            -f
rm ${DIR}/uuid-run            -f

# print the names of the remaining files
echo "build files have been cleaned; remaining files:"
for file in ${DIR}/*; do
    echo ${file##*/}
done
